import React, {useState} from 'react';
import {loginUrl} from '../../api/Api';
import axios from 'axios';
import {Link, useNavigate} from 'react-router-dom';
import {TextField, Button, Typography,Box} from '@mui/material'

const Login = () => {
  const navigate = useNavigate();
  const initialValue = {
    email: 'inem@gmail.com',
    password: '',
  };

  const [data, setData] = useState (initialValue);

  const handleChange = async e => {
    e.preventDefault ();
    setData (prevState => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = async e => {
    e.preventDefault ();
    const userData = {
      email: data.email,
      password: data.password,
    };

    const response = await axios.post(loginUrl, userData);
    console.log ('BC res 1--> ', response);
    if (response.status === 200) {
      console.log ('BC res --> ', response.data);
    }
  };

  const handleNavigate = () => {
    navigate('/forgot-password', {state:{email: data.email}})
  };

  const Styles = {
    FormStyle:{
      width:'70%',
      height:'30%',
      justifyContent:'space-between',
      display:'flex',
      alignItems:'center',
      flexDirection:'column'
    },
    InputStlyes:{
      width: '80%'
    },
    TextFieldStyles:{
      width:'70%',
    },
    WelcText:{
      fontWeight:'700', 
      fontSize: '25px',
      color:'#000',
      mb:'50px'
    },
    LoginMainStyles:{
      display:'flex',
      justifyContent:'center',
      alignItems:'center',
      width:'100%',
      height:'inherit',
      flexDirection: 'column',
      backgroundImage:'linear-gradient(to right, rgba(255,108,0,0),rgba(255,188))'
    },
    SpanStyles:{
      color:'red',
    },
    ForgotPasswordStyle:{
      fontWeight: '700', 
      fontSize: '18px',
      color:'gray'
    },
    Submit:{
      fontsize: '20px',
      fontweight: '700',
      color:'white',
      backgroundColor:'red',
      textTransform: 'none',
      width:'70%'
    }
  }

  return (
    <Box sx = {Styles.LoginMainStyles}>
      <Typography sx={Styles.WelcText}>Welcome Black <Box component = 'span' sx={Styles.SpanStyles}>Favour</Box></Typography>
      <Box component = "form" onSubmit={handleSubmit} sx= {Styles.FormStyle}>
        <Box sx = {Styles.InputStlyes}>
        <TextField 
        sx={Styles.TextFieldStyles}
        value = {data.email}
        name = "email"
        onChange={handleChange}
        type = "email"
        id="email" 
        label="Email" 
        variant="outlined" />
        
      </Box>
        <Box>
        <TextField
        value = {data.password}
        name = "password"
        onChange={handleChange}
        type = "password" 
        id="password" 
        label="password" 
        variant="outlined" />
          {/* <label htmlFor="">
            password:
            <input
              value={data.password}
              name="password"
              onChange={handleChange}
              type="password"
            />
          </label> */}
       </Box>
        <Box style ={{backgroundColour: 'papayawhip', width:'50%'}}>
          <Button sx = {Styles.Submit}
           variant = 'contained' type="submit">Submit</Button>
        </Box>
      </Box>
      <Typography sx = {Styles.ForgotPasswordStyle} 
      onClick={handleNavigate}>Forgot Password</Typography>
    </Box>
  );
};

export default Login;
